import express from 'express';
import { db } from '../db';
import { pricing as pricingTable } from '../../shared/schema';
import { eq } from 'drizzle-orm';

const router = express.Router();

// Get all pricing entries
router.get('/', async (req, res) => {
  const allPricing = await db.select().from(pricingTable);
  res.json(allPricing);
});

// Update a pricing entry by id
router.put('/:id', async (req, res) => {
  const { id } = req.params;
  const { price } = req.body;
  // Only update price
  const [updated] = await db.update(pricingTable)
    .set({ price: String(price) })
    .where(eq(pricingTable.id, id))
    .returning();
  if (!updated) return res.status(404).json({ message: 'Not found' });
  res.json(updated);
});

export default router;

products.ts.content:
import express from 'express';
import multer from 'multer';
import path from 'path';
import { fileURLToPath } from 'url';
import { db } from '../db';
import { products as productsTable } from '../../shared/schema';
import { eq } from 'drizzle-orm';

const router = express.Router();

// No in-memory store; use DB

// Multer setup for thumbnail uploads (ESM compatible)
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const upload = multer({
  dest: path.join(__dirname, '../../client/public/uploads'),
});

router.get('/', async (req, res) => {
  const dbProducts = await db.select().from(productsTable);
  res.json(dbProducts);
});

router.post('/', upload.single('thumbnail'), async (req, res) => {
  const { name, description, price } = req.body;
  let thumbnail = '';
  const file = (req as any).file;
  if (file) {
    thumbnail = `/uploads/${file.filename}`;
  }
  const [product] = await db.insert(productsTable).values({ name, description, price, thumbnail }).returning();
  res.status(201).json(product);
});

router.put('/:id', upload.single('thumbnail'), async (req, res) => {
  const { id } = req.params;
  const { name, description, price } = req.body;
  let thumbnail;
  const file = (req as any).file;
  if (file) {
    thumbnail = `/uploads/${file.filename}`;
  }
  const updateData: any = { name, description, price };
  if (thumbnail) updateData.thumbnail = thumbnail;
  const [product] = await db.update(productsTable).set(updateData).where(eq(productsTable.id, id)).returning();
  if (!product) return res.status(404).json({ message: 'Product not found' });
  res.json(product);
});

router.delete('/:id', async (req, res) => {
  const { id } = req.params;
  await db.delete(productsTable).where(eq(productsTable.id, id));
  res.status(204).end();
});

export default router;

queryClient.ts.content:
import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey.join("/") as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

