  app.use('/api/booking', bookingRouter);
  // Gemini chat endpoint
  app.use('/api/gemini-chat', geminiChatRouter);
  // Demo booking endpoints
  app.post("/api/demo-bookings", async (req, res) => {
    try {
      const bookingData = insertDemoBookingSchema.parse(req.body);
      const booking = await storage.createDemoBooking(bookingData);
      res.json(booking);
    } catch (error) {
      res.status(400).json({ 
        message: "Invalid booking data", 
        error: error instanceof Error ? error.message : "Unknown error" 
      });
    }
  });

  app.get("/api/demo-bookings", async (req, res) => {
    try {
      const bookings = await storage.getDemoBookings();
      res.json(bookings);
    } catch (error) {
      res.status(500).json({ 
        message: "Failed to fetch bookings",
        error: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  // Chat endpoints for live demo (Gemini)
  app.post("/api/chat", async (req, res) => {
    try {
      const { message, sessionId, companyName = "your company" } = req.body;
      if (!message) {
        return res.status(400).json({ message: "Message is required" });
      }
      let session;
      if (sessionId) {
        session = await storage.getChatSession(sessionId);
      }
      if (!session) {
        session = await storage.createChatSession({ messages: [] });
      }
      const messages = [...(session.messages as any[]), { role: "user", content: message }];
      // Use Gemini API with Oishy persona
      const aiMessage = await geminiChat(messages, companyName);
      const updatedMessages = [...messages, { role: "assistant", content: aiMessage }];
      await storage.updateChatSession(session.id, updatedMessages);
      res.json({
        response: aiMessage,
        sessionId: session.id
      });
    } catch (error) {
      console.error("Chat error:", error);
      res.status(500).json({ 
        message: "Failed to process chat message",
        error: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type DemoBooking, type InsertDemoBooking, type ChatSession, type InsertChatSession } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Demo bookings
  createDemoBooking(booking: InsertDemoBooking): Promise<DemoBooking>;
  getDemoBookings(): Promise<DemoBooking[]>;

  // Chat sessions
  createChatSession(session: InsertChatSession): Promise<ChatSession>;
  getChatSession(id: string): Promise<ChatSession | undefined>;
  updateChatSession(id: string, messages: any[]): Promise<ChatSession | undefined>;
}

export class MemStorage implements IStorage {
  private demoBookings: Map<string, DemoBooking>;
  private chatSessions: Map<string, ChatSession>;

  constructor() {
    this.demoBookings = new Map();
    this.chatSessions = new Map();
  }

  async createDemoBooking(insertBooking: InsertDemoBooking): Promise<DemoBooking> {
    const id = randomUUID();
    const booking: DemoBooking = {
      ...insertBooking,
      id,
      company: insertBooking.company || null,
      status: "pending",
      createdAt: new Date(),
    };
    this.demoBookings.set(id, booking);
    return booking;
  }

  async getDemoBookings(): Promise<DemoBooking[]> {
    return Array.from(this.demoBookings.values());
  }

  async createChatSession(insertSession: InsertChatSession): Promise<ChatSession> {
    const id = randomUUID();
    const session: ChatSession = {
      id,
      messages: insertSession.messages || [],
      createdAt: new Date(),
    };
    this.chatSessions.set(id, session);
    return session;
  }

  async getChatSession(id: string): Promise<ChatSession | undefined> {
    return this.chatSessions.get(id);
  }

  async updateChatSession(id: string, messages: any[]): Promise<ChatSession | undefined> {
    const session = this.chatSessions.get(id);
    if (session) {
      session.messages = messages;
      this.chatSessions.set(id, session);
      return session;
    }
    return undefined;
  }
}

export const storage = new MemStorage();

