import React, { useEffect, useState } from "react";
import { AdminSidebar } from "@/components/admin-sidebar";
import { useToast } from "@/hooks/use-toast";
import { useQueryClient } from '@tanstack/react-query';
import { invalidateSiteQueries } from '@/lib/siteQueries';

interface PricingItem {
  id: string;
  name: string;
  price: number;
}

export default function EditPricing() {
  const { toast } = useToast();
  const [pricing, setPricing] = useState<PricingItem[]>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetch('/api/pricing').then(res => res.json()).then(setPricing);
  }, []);

  const handlePriceChange = (id: string, value: string) => {
    setPricing(pricing => pricing.map(item => item.id === id ? { ...item, price: Number(value) } : item));
  };

  const queryClient = useQueryClient();

  const handleSave = async (id: string) => {
    setLoading(true);
    const item = pricing.find(p => p.id === id);
    if (!item) return;
    const res = await fetch(`/api/pricing/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ price: item.price }),
    });
    if (res.ok) {
      toast({ title: 'Pricing updated!', variant: 'default' });
      // Invalidate queries so frontend reloads
      invalidateSiteQueries(queryClient);
    } else {
      toast({ title: 'Error', description: 'Failed to update pricing', variant: 'destructive' });
    }
    setLoading(false);
  };

  return (
    <div className="flex min-h-screen">
      <AdminSidebar />
      <div className="flex-1 max-w-2xl mx-auto py-12 px-4">
        <h1 className="text-2xl font-bold mb-6">Edit Pricing</h1>
        <div className="space-y-4">
          {pricing.map(item => (
            <div key={item.id} className="flex items-center gap-4 border rounded p-4">
              <div className="flex-1 font-semibold">{item.name}</div>
              <input
                type="number"
                value={item.price}
                onChange={e => handlePriceChange(item.id, e.target.value)}
                className="border p-2 rounded w-32"
              />
              <button
                className="bg-primary text-white px-4 py-2 rounded"
                onClick={() => handleSave(item.id)}
                disabled={loading}
              >
                Save
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

client/src/pages/admin-products.tsx.content:
import React, { useEffect, useState } from "react";
import { AdminSidebar } from "@/components/admin-sidebar";
import { useToast } from "@/hooks/use-toast";
import { useQueryClient } from '@tanstack/react-query';
import { invalidateSiteQueries } from '@/lib/siteQueries';

interface Product {
  id: string;
  name: string;
  description: string;
  price: string;
  tag?: string | null;
  status: 'published' | 'upcoming';
  thumbnail?: string;
  features?: string[];
}

export default function ManageProducts() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [products, setProducts] = useState<Product[]>([]);
  const [form, setForm] = useState<Partial<Product> & { file?: File | null }>({
    status: 'published',
    features: []
  });
  const [editingId, setEditingId] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const fetchProducts = async () => {
    const res = await fetch('/api/products');
    setProducts(await res.json());
  };

  useEffect(() => { fetchProducts(); }, []);

  const handleInput = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, files } = e.target as any;
    if (name === 'file') {
      setForm(f => ({ ...f, file: files[0] }));
    } else {
      setForm(f => ({ ...f, [name]: value }));
    }
  };

  const addFeature = () => {
    setForm(f => ({ ...f, features: [...(f.features || []), ''] }));
  };

  const removeFeature = (index: number) => {
    setForm(f => ({ 
      ...f, 
      features: (f.features || []).filter((_, i) => i !== index) 
    }));
  };

  const updateFeature = (index: number, value: string) => {
    setForm(f => ({ 
      ...f, 
      features: (f.features || []).map((feature, i) => i === index ? value : feature) 
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    const formData = new FormData();
    formData.append('name', (form as any).name || '');
    formData.append('description', (form as any).description || '');
    formData.append('price', String((form as any).price || ''));
    if ((form as any).tag) formData.append('tag', (form as any).tag);
    formData.append('status', (form as any).status || 'published');
    if (form.features && form.features.length > 0) {
      formData.append('features', JSON.stringify(form.features.filter(f => f.trim() !== '')));
    }
    if (form.file) formData.append('thumbnail', form.file as any);
    const method = editingId ? 'PUT' : 'POST';
    const url = editingId ? `/api/products/${editingId}` : '/api/products';
    const res = await fetch(url, { 
      method, 
      body: formData,
      headers: {
        'Authorization': 'Bearer admin-token'
      }
    });
    if (res.ok) {
      toast({ title: editingId ? 'Product updated!' : 'Product added!', variant: 'default' });
      invalidateSiteQueries(queryClient);
      setForm({ status: 'published', features: [] });
      setEditingId(null);
      fetchProducts();
    } else {
      toast({ title: 'Error', description: 'Failed to save product', variant: 'destructive' });
    }
    setLoading(false);
  };

  const handleEdit = (product: Product) => {
    setForm({ ...product, file: null });
    setEditingId(product.id);
  };

  const handleDelete = async (id: string) => {
    const res = await fetch(`/api/products/${id}`, { 
      method: 'DELETE',
      headers: {
        'Authorization': 'Bearer admin-token'
      }
    });
    if (res.ok) {
      toast({ title: 'Product deleted!', variant: 'default' });
      invalidateSiteQueries(queryClient);
      fetchProducts();
    } else {
      toast({ title: 'Error', description: 'Failed to delete product', variant: 'destructive' });
    }
  };

  return (
    <div className="flex min-h-screen">
      <AdminSidebar />
      <div className="flex-1 max-w-3xl mx-auto py-12 px-4">
        <h1 className="text-2xl font-bold mb-6">Manage Products</h1>
        <form className="mb-8 space-y-4" onSubmit={handleSubmit}>
          <input 
            data-testid="input-product-name"
            name="name" 
            value={(form as any).name || ''} 
            onChange={handleInput} 
            placeholder="Product Name" 
            className="border p-2 rounded w-full" 
            required 
          />
          <textarea 
            data-testid="input-product-description"
            name="description" 
            value={(form as any).description || ''} 
            onChange={handleInput} 
            placeholder="Description" 
            className="border p-2 rounded w-full" 
            rows={3}
            required 
          />
          <input 
            data-testid="input-product-price"
            name="price" 
            type="text" 
            value={(form as any).price || ''} 
            onChange={handleInput} 
            placeholder="Price (e.g., $299, Free, Contact Us)" 
            className="border p-2 rounded w-full" 
            required 
          />
          <input 
            data-testid="input-product-tag"
            name="tag" 
            value={(form as any).tag || ''} 
            onChange={handleInput} 
            placeholder="Tag (optional, e.g., Most Popular, New)" 
            className="border p-2 rounded w-full" 
          />
          <select 
            data-testid="select-product-status"
            name="status" 
            value={(form as any).status || 'published'} 
            onChange={handleInput} 
            className="border p-2 rounded w-full"
            required
          >
            <option value="published">Published</option>
            <option value="upcoming">Upcoming</option>
          </select>
          <input 
            data-testid="input-product-thumbnail"
            name="file" 
            type="file" 
            accept="image/*" 
            onChange={handleInput} 
            className="w-full" 
          />
          {form.file && (
            <div className="text-sm text-green-600">Selected: {form.file.name}</div>
          )}
          {editingId && (form as any).thumbnail && (
            <div className="text-sm text-gray-500">
              Current thumbnail: <img src={(form as any).thumbnail} alt="Current" className="inline w-8 h-8 object-cover rounded ml-1" />
            </div>
          )}

          {/* Features Section */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-700">
              Feature Lines (appears below description)
            </label>
            {(form.features || []).map((feature, index) => (
              <div key={index} className="flex gap-2">
                <input
                  data-testid={`input-feature-${index}`}
                  type="text"
                  value={feature}
                  onChange={(e) => updateFeature(index, e.target.value)}
                  placeholder={`Feature ${index + 1}`}
                  className="border p-2 rounded flex-1"
                />
                <button
                  data-testid={`button-remove-feature-${index}`}
                  type="button"
                  onClick={() => removeFeature(index)}
                  className="px-3 py-2 text-red-600 hover:bg-red-50 rounded border border-red-200"
                >

                </button>
              </div>
            ))}
            <button
              data-testid="button-add-feature"
              type="button"
              onClick={addFeature}
              className="text-blue-600 hover:underline text-sm"
            >
              + Add Feature Line
            </button>
          </div>
          <button 
            data-testid="button-save-product"
            type="submit" 
            className="bg-primary text-white px-4 py-2 rounded hover:bg-primary/90 disabled:opacity-50" 
            disabled={loading}
          >
            {loading ? 'Saving...' : (editingId ? 'Update Product' : 'Add Product')}
          </button>
          {editingId && (
            <button 
              data-testid="button-cancel-edit"
              type="button" 
              className="ml-2 text-sm underline" 
              onClick={() => { setEditingId(null); setForm({ status: 'published', features: [] }); }}
            >
              Cancel
            </button>
          )}
        </form>
        <div className="grid gap-6">
          {products.map(product => (
